// import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import http from '../../http';
import testData from '../../test.json'

export default function Category() {
    console.log("hii hardik category call");
    // console.log(testData);

    const [Categories, setCategories] = useState([]);

    // console.log(testData.data);

    // testData.data.map(res => {
    //     console.log(res.id);
    // })

    // useEffect(() => {
    //     // console.log("hii hardik kanzariya");
    //     // http.get("/category").then(res => {
    //     //     setCategories(res.data);
    //     // })

    //     // return () => {
    //     //     http.get("/category").then(res => {
    //     //         setCategories(res.data);
    //     //     })
    //     // };

    // }, []);

    const fetchCategoryData = () => {
        fetch("http://127.0.0.1:8000/api/v1/category")
            .then(response => {
                return response.json()
            })
            .then(data => {
                setCategories(data.data)
            })
    }

    useEffect(() => {
        fetchCategoryData()
    }, [])

    const handleDeleteCategory = (id) => {
        // console.log(id);
    }

    // console.log(Categories);

    return (
        <div className='container' style={{ marginTop: 30 }}>
            <div style={{ float: 'right', margin: '10px' }}>
                <Link className='btn btn-primary' to="/add-category"> Add Category </Link>
                {/* <button className='btn btn-primary'>Add Category</button> */}
            </div>

            <table className="table table-hover">
                <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">First</th>
                        <th scope="col">Last</th>
                        <th scope="col">Handle</th>
                        <th scope="col"> Action </th>
                    </tr>
                </thead>
                <tbody>
                    {Categories.length > 0 && (
                        Categories.map((res, index) => (
                            <tr key={index}>
                                <td>{res.id}</td>
                                <td>{res.title}</td>
                                <td>{res.description}</td>
                                <td>{res.is_active == "y" ? "active" : "inactive"}</td>
                                <td> <Link className='btn btn-warning' to={{ pathname: "/edit-category/" + res.id }} > Edit  </Link>
                                    <button className='btn btn-danger' onClick={eve => handleDeleteCategory(res.id)} > Delete  </button>
                                </td>

                            </tr>
                        ))
                    )}

                </tbody>
            </table>
        </div>
    )
}
