import React, { useState, useEffect } from 'react';

export default function AddCategory() {
    const [inputs, setInputs] = useState({
        'title': '',
        'description': '',
        'testing': '',
    });

    const handleChange = (eve) => {
        // console.log("hii title call");
        const name = eve.target.name;
        // console.log(name);
        const value = eve.target.value;
        console.log(value);
        setInput(values => ({ ...values, [name]: value }))
    }

    return (
        <div className='container' style={{ marginTop: "20px" }}>
            <form>
                <div className="form-group">
                    <label htmlFor="title"> Title </label>
                    <input type="text" className="form-control" value={inputs.name || ''} onChange={handleChange} name="title" id="title" placeholder="Enter Category Name" />
                </div>

                <div className="form-group">
                    <label htmlFor="testing"> testing </label>
                    <input type="text" className="form-control" value={inputs.name || ''} onChange={handleChange} name="testing" id="testing" placeholder="Enter Category Name" />
                </div>

                <div className="form-group">
                    <label htmlFor="description"> Description </label>
                    <input type="text" className="form-control" value={inputs.description || ''} onChange={handleChange} name="description" id="description" placeholder="Enter Category Description" />
                </div>

                <div className="form-group">
                    <div className="form-check form-check-inline">
                        <input className="form-check-input" type="radio" name="is_active" id="inlineRadio1" value="y" />
                        <label className="form-check-label" htmlFor="inlineRadio1">Active</label>
                    </div>
                    <div className="form-check form-check-inline">
                        <input className="form-check-input" type="radio" name="is_active" id="inlineRadio2" value="n" />
                        <label className="form-check-label" htmlFor="inlineRadio2">In Active</label>
                    </div>
                </div>

                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}
